
CREATE MEMORY TABLE "PUBLIC"."PERSONA"(

    "PERSONA_ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL,

    "DIRECCION" CHARACTER VARYING(255),

    "EDAD" INTEGER,

    "GENERO" CHARACTER VARYING(255),

    "IDENTIFICACION" CHARACTER VARYING(255),

    "NOMBRE" CHARACTER VARYING(255),

    "TELEFONO" CHARACTER VARYING(255)

);

ALTER TABLE "PUBLIC"."PERSONA" ADD CONSTRAINT "PUBLIC"."PERSONA_ID_PK" PRIMARY KEY("PERSONA_ID");


CREATE MEMORY TABLE "PUBLIC"."CLIENTE"(

    "CLIENTE_ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL,

    "CONTRASENA" CHARACTER VARYING(255),

    "ESTADO" BOOLEAN,

    "PERSONA_ID" BIGINT

);
ALTER TABLE "PUBLIC"."CLIENTE" ADD CONSTRAINT "PUBLIC"."CLIENTE_ID_PK" PRIMARY KEY("CLIENTE_ID");

ALTER TABLE "PUBLIC"."CLIENTE" ADD CONSTRAINT "PUBLIC"."PERSONA_ID_FK" FOREIGN KEY("PERSONA_ID") REFERENCES "PUBLIC"."PERSONA"("PERSONA_ID") NOCHECK;


CREATE MEMORY TABLE "PUBLIC"."CUENTA"(

    "CUENTA_ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL,

    "ESTADO" BOOLEAN,

    "NUMERO_CUENTA" CHARACTER VARYING(255),

    "SALDO_INICIAL" DOUBLE PRECISION,

    "TIPO_CUENTA" CHARACTER VARYING(255),

    "CLIENTE_ID" BIGINT

);
ALTER TABLE "PUBLIC"."CUENTA" ADD CONSTRAINT "PUBLIC"."CUENTA_ID_PK" PRIMARY KEY("CUENTA_ID");

ALTER TABLE "PUBLIC"."CUENTA" ADD CONSTRAINT "PUBLIC"."CLIENTE_ID_FK" FOREIGN KEY("CLIENTE_ID") REFERENCES "PUBLIC"."CLIENTE"("CLIENTE_ID") NOCHECK;


CREATE MEMORY TABLE "PUBLIC"."MOVIMIENTO"(

    "MOVIMIENTO_ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL,

    "FECHA" TIMESTAMP,

    "SALDO" DOUBLE PRECISION,

    "TIPO_MOVIMIENTO" CHARACTER VARYING(255),

    "VALOR" DOUBLE PRECISION,

    "CUENTA_ID" BIGINT

);
ALTER TABLE "PUBLIC"."MOVIMIENTO" ADD CONSTRAINT "PUBLIC"."MOVIMIENTO_ID_PK" PRIMARY KEY("MOVIMIENTO_ID");

ALTER TABLE "PUBLIC"."MOVIMIENTO" ADD CONSTRAINT "PUBLIC"."CUENTA_ID_FK" FOREIGN KEY("CUENTA_ID") REFERENCES "PUBLIC"."CUENTA"("CUENTA_ID") NOCHECK;






